export const _generateUniqueId = () => {
    const uniqueId = () => {
        let str = 'abcdefghijklmnopqrstuvwxyz0123456789ABCDEFGHIJKLMOOPQRSTUVWXYZ';
        let char = '',
            genID = '';
        while(genID.length < 20) {
            char = str.charAt(Math.floor(Math.random() * str.length));
            genID += char;
        }
        return genID;
    }
    return uniqueId()+'-'+uniqueId()+'-'+uniqueId()+'-'+uniqueId();
}

export function _getClassWisePaymentData(unit,batch){
    if(batch === 1) {
        switch (unit) {
            case 1:
            case 2:
            case 3:
            case 4:
                return 2000;
            case 5:
            case 6:
            case 7:
            case 8:
                return 2500;
            case 9:
            case 10:
                return 3000;
        }
    }else if(batch === 2){
        switch (unit) {
            case 1:
            case 2:
            case 3:
            case 4:
                return 1800;
            case 5:
            case 6:
            case 7:
            case 8:
                return 2200;
            case 9:
            case 10:
                return 2700;
        }
    }else if(batch === 3){
        switch (unit) {
            case 1:
            case 2:
            case 3:
            case 4:
                return 1700;
            case 5:
            case 6:
            case 7:
            case 8:
                return 2100;
            case 9:
            case 10:
                return 2500;
        }
    }
}
